import React, { useContext, useEffect, useState } from 'react'
import { globalContext } from './Context';

const mainStyle = {
  margin: "10px",
  maxWidth: "100%",
  border: "5px soild red",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  flexWrap: "wrap",
  gap: "1.5rem",
};

const mainIndividualDiv = {
  height: "auto",
  width: "300px",
  border: "1px soild red",
  boxSizing: 'border-box',
  backgroundColor: "grey",
  alignItems: "center",
  margin: "0 auto",
  color:"white",
}

export default function Main() {
  const {user, setUser} = useContext(globalContext);
  const [editIndex, setEditIndex] = useState(-1);
  const [userInfoEdit, setUserInfoEdit] = useState({
    name: "",
    city: "",
    pincode: "",
    country: "",
  });
  console.log(user);
  console.log(userInfoEdit)
  console.log(editIndex);
  return (
    <div style={mainStyle}>
      {
        user.map((ele, idx) => 
          editIndex !== idx ? (
            <div style={mainIndividualDiv} key={ele.name + idx}>
                <ul style={{ listStyle: "none" }}>
                  <li>{ele.name}</li>
                  <li>{ele.city}</li>
                  <li>{ele.pincode}</li>
                  <li>{ele.country}</li>
                </ul>
              <div style={{ display: "flex", justifyContent: "space-evenly" }}>
                <button onClick={()=>setEditIndex(idx)}>Edit</button>
                <button
                  onClick={() => {
                    setUser([...user.filter((ele, id) => id !== idx)]);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ) : (
            <div style={mainIndividualDiv} key={ele.name + idx}>
                <ul style={{ listStyle: "none" }}>
                  <li><input defaultValue={user[idx].name} onChange={(e)=>setUserInfoEdit({...userInfoEdit,name:e.target.value})}/></li>
                  <li><input defaultValue={user[idx].city} onChange={(e)=>setUserInfoEdit({...userInfoEdit,city:e.target.value})}/></li>
                  <li><input defaultValue={user[idx].pincode} onChange={(e)=>setUserInfoEdit({...userInfoEdit,pincode:e.target.value})}/></li>
                  <li><input defaultValue={user[idx].country} onChange={(e)=>setUserInfoEdit({...userInfoEdit,country:e.target.value})}/></li>
                  <button onClick={()=>{let t=[...user];t[editIndex] = userInfoEdit;console.log(t);setUser([...t]);setEditIndex(-1)}}>Save</button>
                </ul>

              <div style={{ display: "flex", justifyContent: "space-evenly" }}>
                <button onClick={()=>setEditIndex(idx)}>Edit</button>
                <button
                  onClick={() => {
                    setUser([...user.filter((ele, id) => id !== idx)]);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          )
        )
      }
    </div>
  );
}
