import React from 'react'

function MultipleChoise({questions, setQuestions}) {

    function handleAnswer(e){
        let temp = [...questions];
        temp[1].question.answer = e.target.id;
        setQuestions(temp);
    }

    return (
      <div>
        <h1>{questions.question}</h1>
        {questions.option.map((opt) => (
          <div key={option}>
            <input
              type="radio"
              id={opt}
              name={opt}
              value={opt}
              onChange={handleAnswer}
            />
          </div>
        ))}
      </div>
    );
}

export default MultipleChoise
