import React, { useEffect, useState } from "react";
import image1 from "./1.jpeg";
import image2 from "./2.jpeg";
import image3 from "./3.jpeg";
import image4 from "./4.jpeg";

const arr = [image1, image2, image3, image4];
export default function Crousal() {
  const [page, setPage] = useState(1);
  const handleNext = () => {
    setPage(page + 1);
  };

  const handlePrev = () => {
    setPage(page - 1);
  };
  let k = null;
  useEffect(() => {
    if (page === 3) {
      setPage(0);
    }
    k = setInterval(() => {
      setPage((pre) => pre + 1);
    }, 2000);
    return () => clearInterval(k);
  }, [page]);

  console.log(page);
  return (
    <>
      <div>
        <div
          style={{
            height: "70vh",
            width: "100%",
            border: "1px soild black",
            display: "flex",
            justifyContent: "space-around",
          }}
        >
          <div
            style={{
              width: "10%",
              height: "100%",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <button onClick={handlePrev}>Prev</button>
          </div>
          <div>
            <img style={{ height: "300px" }} src={arr[page]} alt="image" />
          </div>
          <div
            style={{
              width: "10%",
              height: "100%",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <button onClick={handleNext}>Next</button>
          </div>
        </div>
      </div>
      
    </>
  );
}
