import React, {useState} from "react";
import Pad from "./Pad";
import { bank1 } from "./App";

function Pads({power}) {
  const keypadCode = Object.keys(bank1);
  const [currentKey, setCurrentKey] = useState(null);

  function playSound(pad) {
    return function (event) {
      setCurrentKey(pad);
      new Audio(bank1[pad].source).play();
    };
  }

  

  return (
    <div id="div-pads">
      {keypadCode.map((pad, idx) => {
        
        return (
          <Pad
            id={pad + idx}
            key={pad + idx}
            handleClick={playSound(pad)}
            power={power}
            element={pad}
          />
        );
      })}
      <div id="display"> {currentKey} </div>
    </div>
  );
}

export default Pads;
