import React, {useReducer,} from 'react';
import './App.css';
import C1 from './C1';
import C2 from './C2';
import C3 from './C3';



const initialcount =0;
export const dis = React.createContext();
const reduce = (state, action)=>{
  switch(action){
    case 'increment':
      return state+1
    case 'decrement':
      return state-1;
    case 'reset':
      return initialcount;
  }
}

export default function App(){
  const [state, dispatch] = useReducer(reduce, initialcount);
  return (
    <div className="App">
      <dis.Provider value={{state:state, CountDispatch:dispatch}}>
        <C1 />
        <C2 />
        <C3 />
      </dis.Provider>
    </div>
  );
}
