import React from 'react'
const App = () => {

  const handleClick = (event) =>{
    // use console.log
    console.log(`Button id is:-${event.target.id}`)
  }

  // do not remove the two buttons or change their id
  return (
    <div id="main">
      <button onClick ={handleClick(event)}>Button A</button>
      <button onClick ={handleClick(event)} id="button-b">Button B</button>
    </div>
  )
}


export default App;
