import axios from 'axios';
import React, {useEffect, useReducer, useState} from 'react';
import './App.css';

const intialState ={loading:true, error: "", post: {}};

const reducer = (state, action)=>{
  switch(action.type){
    case 'SUCCESS':
      return {
        loading: false,
        error: "",
        post: action.payload,
      }
    case 'error':
      return {
        loading: false,
        error: "Something Wrong",
        post: {},
      }
    default:
      return state;
  }
}

export default function App(){
  const [state, dispatch] = useReducer(reducer, intialState);
  const [page, setPage] = useState('1')


  useEffect(()=>{

    axios.get(`https://jsonplaceholder.typicode.com/posts/${page}`)
    .then(res =>{
      dispatch({type: 'SUCCESS', payload: res.data});
    })
    .catch(error =>{
      dispatch({type: 'error'});
    })
  },[])

  console.log(state.post);

  return (
      <div className="App">
        <div className='page-container'>
            <h1>{state.post.userId}</h1>
            <h1>{state.post.id}</h1>
            <h1>{state.post.title}</h1>
        </div>
        <div className='page-nation'>
            <nav aria-label="Page navigation example">
              <ul class="pagination">
                <li class="page-item"><button>Previous</button></li>
                <li class="page-item"><button onClick = {()=>{setPage('1')}}>ONE</button></li>
                <li class="page-item"><button onClick = {()=>{setPage('2')}}>TWO</button></li>
                <li class="page-item"><button onClick = {()=>{setPage('3')}}>THREE</button></li>
                <li class="page-item"><button>Next</button></li>
              </ul>
            </nav>
          </div>
      </div>
  );
}
