import React, { useEffect, useState } from 'react'
import './App.css'

const winState = [];
for(let i=0; i<3; i++){
  let col = [];
  for(let j=0; j<3; j++){
    col[j]=0;
  }
  winState.push(col);
}

export default function App() {
  const [state, setState] = useState('');

  // console.log(winState);
  let board =[];
  useEffect(()=>{
    for(let i=0; i<3; i++){
      for(let j=0; j<3; j++){
        board.push(
          <div
            key = {`${i}-${j}`}
            style ={{height:'50px', width:'50px', border:'1px solid black'}}
          ><button onClick={setState('O')} style ={{height:'50px', width:'50px', border:'1px solid black'}}><h2>{state}</h2></button></div>
        )
      }
    }
  }, [state])

  return (
      <div className='App' style={{height:'150px', width: '150px', display:'flex', flexWrap:'wrap'}}>
        {board}
      </div>
  )
}
