import React, {useReducer,} from 'react';
import './App.css';
import C1 from './C1';




const initialcount =0;
export const dis = React.createContext();
const reduce = (state, action)=>{
  switch(action){
    case 'increment':
      return state+1
    case 'decrement':
      return state-1;
    case 'reset':
      return initialcount;
  }
}

export default function App(){
  const [state, dispatch] = useReducer(reduce, initialcount);
  return (
    <dis.Provider value={{CountState:state, CountDispatch:dispatch}}>
    <div className="App">
      <C1 />
    </div>
    </dis.Provider>
  );
}
