import React from 'react'
import { useDispatch } from 'react-redux';
import {playButtonfunc} from './redux/actionType'

const buttonPlayerStyle = {
  width: "48%",
  display: "flex",
  justifyContent: "center",
  flexDirection: "column",
};


export default function ButtonPlayer() {
  const dispatch = useDispatch();


  return (
    <div style={buttonPlayerStyle}>
      {[...Array(3)].map((_, idx) => (
        <button onClick={dispatch(playButtonfunc(idx))}>Click+{idx + 1}</button>
      ))}
    </div>
  );
}
