export default function App() {
  const [page, setPage] = useState(0);
  const [flag, setFlag] = useState(false);
  function handleClick(e){
    let scrollHeight = e.target.scrollHeight
    let scrollTop = e.target.scrollTop;
    let clientHeight = e.target.clientHeight;
    if(Math.ceil(scrollTop + clientHeight) ===scrollHeight){
      setFlag(true);
      setTimeout(()=>{
        setPage(page+1);
        setFlag(false);
      }, 2000)
    }
  }

  return (
    <div onScroll={handleClick} style={{ overflowY: "scroll", height:"100vh" }}>
      flag ? <h1>Loading.........</h1>
      {user.slice(page*10, page*10+10).map((ele, idx) => (
        <div style={{ height: "200px", border: "1px solid red" }} key={idx+idx}>
          <h1>{idx + 1}</h1>
          <h1>{ele.name}</h1>
          <h3>{ele.city}</h3>
        </div>
      ))}
    </div>
    
  );
}
