function ControlScreen() {
  function handleVolumeChange(event) {
    const volume = event.target.value;
    const audioElements = document.querySelectorAll(".clip");
    audioElements.forEach((audio) => {
      audio.volume = volume / 100;
    });
    document.getElementById("volume-display").textContent = `Volume: ${volume}`;
  }

  return (
    <div id="control-screen">
      <label id="label-power">
        <input type="checkbox" id="power" />
        <span className="checkmark">Show Power of off here </span>
      </label>
      <label id="label-volume">
        <input
          type="range"
          id="volume"
          min="0"
          max="100"
          defaultValue="50"
          onChange={handleVolumeChange}
        />
        <span id="volume-display">Volume: 50</span>
      </label>
    </div>
  );
}
