import React, { useState } from "react";
// import ring from "./jewlRing.jpg";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { globalState } from "../Context";
import ProductCard from "./ProductCard";
const Sell = () => {
  const navigate=useNavigate();
  const[productInfo,setProductInfo]=useState({
    productImage:"",
    productTitle:"",
    category:"",
    material:"",
    price:"",
    productDetails:""
  })
  const { todo, setTodo } = useContext(globalState);
  const{ productImage,productTitle,category,materialprice,productDetails}= productInfo;
  const handleOnChange = (e) => {
    setProductInfo({ ...productInfo, [e.target.name]: e.target.value });
  };
  console.log(productInfo);

  const handleOnClick = (e) => {
    e.preventDefault();
    let addItems = [...todo];
    addItems.unshift(productInfo);
    setTodo(addItems);
    navigate("/");
    setProductInfo({
      productImage:"",
      productTitle:"",
      category:"",
      material:"",
      price:"",
      productDetails:""
    });
  };
  return (
    <div>
      <section id="form-details">
        <div className="ringImgContainer">
          {/* <img src={ring} alt="" style={{width:"480px",height:"500px"}}/> */}
          <ProductCard productInfo={productInfo}/>
        </div>
        <form action="">
          <span>Latest Items</span>
          <h2>Add New Items Here</h2>
          <input type="file" name="productImage"  onChange={handleOnChange}/>
          <input type="text" placeholder="Title" name="productTitle"  onChange={handleOnChange}/>
          <label for="category">Category:</label>

          <select name="category" id="category">
            <option value="ring">Ring</option>
            <option value="earring">Earring</option>
            <option value="bracelets & bangles">Bracelets & Bangles</option>
            <option value="pendants">Pendants</option>
            <option value="necklaces">Necklaces</option>
            <option value="nose ring">Nose rings</option>

          </select>
          <label for="material">Material:</label>

          <select name="material" id="material">
            <option value="volvo">Rose Gold</option>
            <option value="saab">White Gold</option>
            <option value="mercedes">Yellow Gold</option>
            <option value="audi">Platinum</option>
            <option value="audi">Diamond</option>
            <option value="audi">Gemstone</option>
            <option value="audi">22KT Gold</option>
            <option value="audi">24KT Gold</option>
            <option value="audi">Silver</option> 
          </select>
          <input type="number" placeholder="Price" name="price" />
          <textarea name="productDetails" id="" cols="30" rows="5"  onChange={handleOnChange}>
           About Jwellary
          </textarea>
          <button class="normal" onClick={handleOnClick}>Add Items</button>
        </form>
      </section>
    </div>
  );
};

export default Sell;
