import React, {useState} from 'react'

export default function Child({name,desc}) {
    const [flag, setFlag] = useState(false);
  return (
    <div
      style={{ height: "100px", border: "1px solid red", alignItems:"center" }}
      onClick={() => setFlag(!flag)}
    >
      <h1>{name}</h1>
      <h3>{flag && desc}</h3>
    </div>
  );
}
