  }
  const chooseSqare =(id)=>{
    setBoard(board.map((ele,idx)=>
      {
        if(idx===id && ele==='') return player;
        return ele;
      }
    ));
    if(player ==='X') setPlayer('O');
    else setPlayer('X');
  }

  return (
    <div className='App'>
        <div className='board'>
            <div className='row'>
                <Square val = {board[0]} chooseSqare = {()=>chooseSqare(0)}/>
                <Square val = {board[1]} chooseSqare = {()=>chooseSqare(1)}/>
                <Square val = {board[2]} chooseSqare = {()=>chooseSqare(2)}/>
            </div>
            <div className='row'>
              <Square val = {board[3]} chooseSqare= {()=>chooseSqare(3)}/>
              <Square val = {board[4]} chooseSqare= {()=>chooseSqare(4)}/>
              <Square val = {board[5]} chooseSqare= {()=>chooseSqare(5)}/>
            </div>
            <div className='row'>
              <Square val = {board[6]} chooseSqare= {()=>chooseSqare(6)}/>
              <Square val = {board[7]} chooseSqare= {()=>chooseSqare(7)}/>
              <Square val = {board[8]} chooseSqare= {()=>chooseSqare(8)}/>
            </div>
        </div>
    </div>
  )
}
