import React from "react";

function Card({ newsImage, title, dateShow, authors, link, excerpt }) {
  return (
    <div className="flip-card">
      <div className="flip-card-inner">
        <div className="flip-card-front">
          <img src={newsImage} alt="Avatar" />
          <p>
            {dateShow}-- {title}
          </p>
        </div>

        <div className="flip-card-back">
          <div className="flip-card-author">{authors}</div>
          <div className="flip-card-link">{link}</div>
          <div className="flip-card-excerpt">{excerpt}</div>
        </div>
      </div>
    </div>
  );
}

export default Card;
