import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      counter: 0,
    };

  }
  increment = ()=> {
    this.setState({counter: this.state.counter+1})
  }

  render() {
    return (
      <div>
        <button onClick={this.increment}>Increment</button>
        <h1>{this.state.counter}</h1>
      </div>
    );
  }
}

export default App;
