import React, { useState } from "react";
import "./styles.css";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [showAccount, setShowAccount] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSearch = () => {
    setShowSearch(!showSearch);
  };

  const handleAccount = () => {
    setShowAccount(!showAccount);
  };
  console.log(window.innerWidth)
  return (
    <nav className="navbar">
      <div className="navbar-brand">
        {window.innerWidth < 768 ? (
          <i className="fa fa-jewelry" onClick={handleToggle} />
        ) : (
          "JewellerCommerce"
        )}
      </div>
      <div className="navbar-search">
        <input
          type="text"
          placeholder="Search"
          className={showSearch ? "show" : ""}
        />
        {showSearch ? (
          <i className="fa fa-search" onClick={handleSearch} />
        ) : (
          <i className="fa fa-search" onClick={handleSearch} />
        )}
      </div>
      <div className="navbar-account">
        {window.innerWidth < 768 ? (
          <i className="fa fa-user-circle" onClick={handleAccount} />
        ) : (
          <div className="dropdown" onClick={handleToggle}>
            <div className="dropdown-toggle">
              Account <i className={`arrow ${isOpen ? "up" : "down"}`} />
            </div>
            <div className={`dropdown-menu ${isOpen ? "show" : ""}`}>
              <a href="#">Profile</a>
              <a href="#">Settings</a>
              <a href="#">Logout</a>
              <hr />
              <a href="#">Login</a>
            </div>
          </div>
        )}
      </div>
      {showAccount && (
        <div className="account-menu">
          <a href="#">Profile</a>
          <a href="#">Settings</a>
          <a href="#">Logout</a>
          <hr />
          <a href="#">Login</a>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
