import React, { useEffect } from "react";

export default function useComponentDidUpdate(fun, array) {
  const nameRef = useRef(false);
  useEffect(() => {
    if (nameRef.current) {
      fun();
    } else {
      nameRef.current = true;
    }
  }, array);
}
