import React, { Component } from 'react';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 5,
    };

  }
  handleUpdater = ()=>{
    this.setState({value: this.state.value+1});
  }

  render() {

    return (
      <div>
        <h1>{this.state.count}</h1>
        <button onClick={this.handleUpdater}>Up</button>
      </div>
    );
  }
}
