import React, { useContext, useState } from 'react'
import { globalContext } from './Context';

const mainStyle = {
  margin: "10px",
  maxWidth: "100%",
  border: "5px soild red",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  flexWrap: "wrap",
  gap: "2.5rem",
};

const mainIndividualDiv = {
  height: "auto",
  width: "300px",
  border: "1px soild red",
  boxSizing: 'border-box',
  backgroundColor: "grey",
  alignItems: "center",
  margin: "0 auto",
}

export default function Main() {
  const {user, setUser} = useContext(globalContext);
  const [editIndex, setEditIndex] = useState(-1);
  const userInfoEdit ={
    name:"",
    city:"",
  }
  console.log(user);
  return (
    <div style={mainStyle}>
      {user.map((ele, idx) => (
        <div style={mainIndividualDiv} key={ele.name + idx}>
          { editIndex!==idx ? (
            <ul style={{ listStyle: "none" }}>
              <li>{ele.name}</li>
              <li>{ele.city}</li>
              <li>{ele.pincode}</li>
              <li>{ele.country}</li>
            </ul>
          ) : (
            <ul style={{ listStyle: "none" }}>
              <li><input value={user[idx].name} onClick={(e)=>setUser({...userInfoEdit,name:e.target.vale})}/></li>
              <li><input value={user[idx].city} onClick={(e)=>setUser({...userInfoEdit,city:e.target.vale})}/></li>
              <li><input value={user[idx].pincode} onClick={(e)=>setUser({...userInfoEdit,pincode:e.target.vale})}/></li>
              <li><input value={user[idx].country} onClick={(e)=>setUser({...userInfoEdit,country:e.target.vale})}/></li>
              <button onClick={()=>set}>Save</button>
            </ul>
            )
          }
          <div style={{ display: "flex", justifyContent: "space-evenly" }}>
            <button onClick={()=>setEditIndex(idx)}>Edit</button>
            <button
              onClick={() => {
                setUser([...user.filter((ele, id) => id !== idx)]);
              }}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
