  const [count, setCount] = useState(0);
  useComponentDidUpdate(()=>{
    console.log("component did update");
  }, [count])

  useEffect(()=>{
    console.log("component did mount")
  },[])
  return (
    <div className='App'>
      <h1>{count}</h1>
      <button onClick={()=>setCount(count +1)}>increment</button>
    </div>
  )

}


export default App;
