import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addTodo, delTodo } from './redux/action';

export default function TodoList() {
    const state = useSelector(state => state);
    console.log(state);
    const dispatch = useDispatch();
    const [text, setText] = useState("");
    const [edit, setEdit] = useState(-1);
  return (
    <div>
        <h1>Bittu's TodoList</h1>
        <input type="text" onChange = {(e)=>setText(e.target.value)}/>
        <button onClick ={()=>dispatch(addTodo(text))}>Add Todo</button>
        {
            state.todoState.todoList.map((ele, ind)=>{
                return (
                    (ind ===edit) ? 
                        <>
                        <input type="text" onChange = {(e)=>setText(e.target.value)}/>
                        <button onClick={()=>{state.todoState.todoList[edit]=text}}>Save</button></>
                    :
                    <h1>
                        {ele}
                        <button onClick={()=>{setEdit(ind)}}>Edit</button>
                        <button onClick={()=>dispatch(delTodo(ind))}>Del</button>
                    </h1>
                    
                )
            })
        }
    </div>
  )
}
