import React, {useEffect, useState} from 'react'
import Timer from './timer';



const App = () => {


  const [arr, setArr] = useState([]);
  const [delid, setId] = useState(null)
  const handleTimer = () => {
    setArr([...arr, 0]);
  };
  const handel = (e)=>{

    let temp = arr.filter((ele,id)=> Number(e.target.id) !== id)
    setArr([temp]);
  }
  return (
    <div className='App'>
      <button onClick={handleTimer}>Add Timer</button>
      {
        arr.map((ele, idx)=>{
          return <div id={idx}>
            <Timer/>
            <button id = {idx} onClick={(e)=>handel(e)}>Del</button>
          </div>
        })
      }

    </div>
  )

}


export default App;
