import React, {useEffect, useState} from 'react'
import Timer from './timer';


const App = () => {
  let array = [];


  const [arr, setArr] = useState([]);
  const handleTimer = () => {
    setArr([...arr, 0]);
  };

  const handel = (e)=>{

    let temp = arr.filter((ele,id)=> Number(e.target.id) !== id)
    setArr([...temp]);
  }
  return (
    <div className='App'>
      <button onClick={handleTimer}>Add Timer</button>
      {
        arr.map((ele, idx)=>{
          (!array.includes(idx)) ?

            <div id={idx}>
              <Timer/>
              <button id = {idx} onClick={array.push(idx)}>Del</button>
            </div>
            :null
        })
      }

    </div>
  )

}


export default App;
