import React from 'react'
import { useSelector } from 'react-redux';
import { DEL_TODOfunc } from "../redux/actionType";
import { useDispatch } from "react-redux";

const mainStyle = {
    display: "flex",
    flexWrap : "wrap",
    justifyContent: "space-around",
}

export default function Main() {
  const dispatch = useDispatch();
  const state = useSelector(state=>state);
  console.log(state);
  return (
    <div style={mainStyle}>
      {state.todoList &&
        state.todoList.map((ele, idx) => (
          <div style={{border: "1px solid black", width:"30%"}} key={ele.name+idx+idx}>
            <h1>{idx+1}</h1>
            <h1>{ele.name}</h1>
            <h1>{ele.email}</h1>
            <h1>{ele.city}</h1>
            <button onClick={()=>dispatch(DEL_TODOfunc(idx))}>DELETE</button>
            <button>Edit</button>
          </div>
        ))}
    </div>
  );
}
