import video1 from "../videos/video1.mp4";
import video2 from "../videos/video2.mp4";
import video3 from "../videos/video1.mp4";
import actionType from './actionType';

const intialState = {
    videos: [video1, video2, video3],
    current: video1,
};

export default function actionReducer(state=intialState, action){
    switch(action.type){
        case "PLAY_BUTTON":
            return {
                ...state,
                current: state.videos[action.value],
            }
        default: 
            return {
                state
            }
    }
}
