import React, { useContext } from 'react'
import { globalContext } from './Context';

const mainStyle = {

  maxWidth: "100%",
  border: "5px soild red",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  flexWrap: "wrap",
  gap: "2.5rem",
};

const mainIndividualDiv = {
  height: "150px",
  width: "300px",
  border: "1px soild red",
  boxSizing: 'border-box',
  backgroundColor: "grey",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
}

export default function Main() {
  const {user} = useContext(globalContext);
  console.log(user);
  return (
    <div style={mainStyle}>
      {
        user.map(ele=>(
          <div style={mainIndividualDiv}>
            <h2>{ele.name}</h2>
            <h3>{ele.city}</h3>
            <h3>{ele.pincode}</h3>
            <h3>{ele.country}</h3>
          </div>
        ))
      }
    </div>
  );
}
