const intialState = {
    todoList: [],
    name: ""
}

export default function todoReducer(state=intialState, action){
    switch(action.type){
        case "ADD_TODO" :
            return{
                ...state,
                todoList: [...state.todoList, action.value]
            }
        case "DELETE_TODO" :
            return {
                ...state,
                todoList: state.todoList.filter((ele,idx)=>idx!==action.value)
            }
        case "EDIT_TODO": {
                let temp = [...state.todoList];
                temp[action.value.index] =action.value.text;
                
                return {
                    ...state,
                    todoList: [...temp]
                }
            }
        default: {
            return state
        }
    }
}
