import React, {} from 'react';

const Square = ({ onClick }) => (
  <div style={{ backgroundColor: 'white', width: '100%', height: '100%' }} onClick={onClick} />
);

const Board = () => {
  const [squares, setSquares] = useState(Array(25).fill(null));

  const handleClick = (index) => {
    const newSquares = [...squares];
    newSquares[index] = 'red';
    setSquares(newSquares);
  };

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', width: '250px', height: '250px' }}>
      {squares.map((square, index) => (
        <Square key={index} onClick={() => handleClick(index)} />
      ))}
    </div>
  );
};

export default Board;
