import { useState } from "react";
import { FaUser } from "react-icons/fa";

function LoginPopup() {
  const [showPopup, setShowPopup] = useState(false);
  const [showLogin, setShowLogin] = useState(true);

  const togglePopup = () => {
    setShowPopup(!showPopup);
    setShowLogin(true);
  };

  const toggleForm = () => {
    setShowLogin(!showLogin);
  };

  const handleLoginSubmit = (event) => {
    event.preventDefault();
    // handle login submission logic
  };

  const handleRegisterSubmit = (event) => {
    event.preventDefault();
    // handle register submission logic
  };

  return (
    <>
      <div className="user-login-container">
        <button onClick={togglePopup} className="user-login-link">
          <FaUser className="user-icon" />
          <span className="user-login-text">Login / Register</span>
        </button>
      </div>
      {showPopup && (
        <div className="popup-container">
          <div className="popup">
            <div className="popup-header">
              <h2>{showLogin ? "Login" : "Register"}</h2>
              <button onClick={togglePopup}>X</button>
            </div>
            <div className="popup-body">
              {showLogin ? (
                <form onSubmit={handleLoginSubmit}>
                  <label htmlFor="email">Email</label>
                  <input type="email" id="email" required />
                  <label htmlFor="password">Password</label>
                  <input type="password" id="password" required />
                  <button type="submit">Login</button>
                  <p>
                    Not registered yet?{" "}
                    <button onClick={toggleForm}>Register now</button>
                  </p>
                </form>
              ) : (
                <form onSubmit={handleRegisterSubmit}>
                  <label htmlFor="name">Name</label>
                  <input type="text" id="name" required />
                  <label htmlFor="email">Email</label>
                  <input type="email" id="email" required />
                  <label htmlFor="password">Password</label>
                  <input type="password" id="password" required />
                  <button type="submit">Register</button>
                  <p>
                    Already registered?{" "}
                    <button onClick={toggleForm}>Login now</button>
                  </p>
                </form>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
}
export default LoginPopup;
