    const [count, setCount] = useState(0);
    const [array, setArray] = useState([]);
    function handleClick(){
        setArray([...array, <Timer setCount/>])
    }
  return (
    <div className="App">
        <button onClick={handleClick}>Add Timer</button>
        {
            array.map((ele,idx)=>(
                ele
            ))
        }
    </div>
  )
}

export default App
