import React, {useState, useEffect} from 'react'
import {DefaultPlayer as Video} from 'react-html5video';
import { useSelector} from "react-redux";
import 'react-html5video/dist/styles.css';
// import video1 from './videos/video1.mp4';



const videoPlayerStyle = {
    width: "50%",
}

export default function VideoPlayer() {
  const state = useSelector((state) => state);
  const [v, setV] = useState(state.state.videos[0]);
  
  function handleClick(e){
    let id = parseInt(e.target.id);
    setV(state.state.videos[id]);
  }
  return (
    <>
      <div style={videoPlayerStyle}>
        <Video autoPlay loop>
          <source src={v} type="video/webm" />
        </Video>
      </div>
      <div style={{display: "flex", flexDirection:"column", width:"50%", marginTop: "100px"}}>
        {[...Array(3)].map((_, idx) => (
          <button id={idx} key={idx} onClick={handleClick}>Click{idx + 1}</button>
        ))}
      </div>
    </>
  );
}
