  const [currentKey, setCurrentKey] = useState(null);

  function playSound(){
    let id = e.target.id;
    console.log(id);
    playSound();
    setCurrentKey(keypadCode.id.name);
    new Audio(keypadCode.id.source).play();
  }

  const handleClick = (e) => {
    
  };

  return (
    <div id="div-pads">
      {keypadCode.map((pad, idx) => {
        
        return (
          <Pad
            id={pad + idx}
            key={pad + idx}
            handleClick={playSound}
            element={pad}
          />
        );
      })}
      <div id="display"> {currentKey} </div>
    </div>
  );
}

export default Pads;
