import React, { useEffect,useState } from 'react'


export default function App() {
  const [cnt, setCnt] = useState(0);
  let k = null;

  useEffect(()=>{
    k = setInterval(()=>{
      setCnt(p=>p=p+1)
    }, 1000)
    return ()=>clearInterval(k);
  }, [cnt])

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <div
        style={{
          width: "130px",
          height: "40px",
          border: "1px solid black",
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        {/* <h3>NEXT</h3> */}
        {
          [...Array(100)].map((ele,idx)=>(
            <div style={{width:"1%", backgroundColor: idx<cnt? "green": "white"}}>

            </div>
          ))
        }
      </div>
    </div>
  );
}
