import { useState } from 'react';
import './App.css';
import Timer from './Timer';

function App() {
    const [array, setArray] = useState([]);
    function handleClick(){
        setArray([...array, <Timer />]);
    }
    console.log(array);
    return (
        <div className="App">
            <button onClick={handleClick}>Add Timer</button>
            {
                array.map((ele,idx)=>(
                    ele
                ))
            }
            
        </div>
    )
}

export default App
