import { createContext, useState } from "react";
const intialUser = [
  {
    name: "Bittu Mondal",
    city: "Kolkata",
    pincode: "713101",
    country: "INDIA",
  },
  {
    name: "Sonam Murakar",
    city: "Nagpur",
    pincode: "510131",
    country: "INDIA",
  },
  {
    name: "Nikita Pawar",
    city: "Delhi",
    pincode: "713201",
    country: "Kanda",
  },
  {
    name: "Vikash Sharma",
    city: "Uttar Pradesh",
    pincode: "810565",
    country: "INDIA",
  },
  {
    name: "Sreeja Mugde",
    city: "Nagpur",
    pincode: "510131",
    country: "Dubai",
  },
  {
    name: "Vikash Dutta",
    city: "Delhi",
    pincode: "713201",
    country: "Kanda",
  },
  {
    name: "Vikash Sharma",
    city: "Uttar Pradesh",
    pincode: "810565",
    country: "INDIA",
  },
];
export const globalContext = createContext();
export default function Context({children}){

    const [user, setUser] = useState(intialUser);
    return(
        <globalContext.Provider value={{user, setUser}}>
            {children}
        </globalContext.Provider>
    )
}
