import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      counter: "hello",
    };

  }
  sayHello = ()=> {
    this.setState({message: this.state.message==="hello"? "sayHello": "hello"})
  }

  render() {
    return (
      <div>
        <button onClick={this.sayHello}>SayHello</button>
        <h1>{this.state.message}</h1>
      </div>
    );
  }
}

export default App;
