import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addTodo } from './redux/action';

export default function TodoList() {
    const [text, setText] =useState();
    const [edit, setEdit] =useState(-1);
    const state = useSelector(state=>state);
    const dispatch = useDispatch();
    console.log(state);
  return (
    <div>
        <h1>Bittu's Todo</h1>
        <input type="text" onChange={(e)=>setText(e.target.value)}/>
        <button onClick={()=>dispatch(addTodo(text))}>Add Todo</button>
        {
            state.todoState.TodoList.map((ele, idx)=>{
                return (
                    (edit !==-1)?
                    <h1>{ele}
                    <button onClick={()=>setEdit(idx)}>Edit</button>
                    <button onClick={()=>dispatch(delTodo(idx))}>Del</button>
                    </h1>
                    :<>
                    <input type="text" onChange={(e)=>setText(e.target.value)}/>
                    <button onClick={()=>state.todoState.TodoList[idx]=text}>Del</button></>
                )
            })
        }

    </div>
  )
}
