const intialState = {
    todoList: [],
}

export default function todoReducer(state=intialState, action){
    switch(action.type){
        case "ADD_TODO" :
            return{
                ...state,
                todoList: [...state.todoList, action.value]
            }
        case "DELETE_TODO" :
            return {
                ...state,
                todoList: state.todoList.filter((ele,idx)=>idx!==action.value)
            }
        case "Edit_TODO": 
            return {
                ...state,
                todoList: [...state]  
            }
        default: {
            return state
        }
    }
}
