const App = () => {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let temp = data.slice((page - 1) * 10, (page - 1) * 10 + 10);
    setItems([...temp]);
  }, []);

  function infiniteScroll(e){
    let scrollHeight = e.target.scrollHeight;
    let scrollTop = e.target.scrollTop;
    
    if (Math.floor(scrollHeight - scrollTop) === Math.floor(e.target.clientHeight)) {
      setLoading(true);
      setTimeout(() => {
        setPage((pre) => pre + 1);
        let temp = data.slice((page - 1) * 10, (page - 1) * 10 + 10);
        setItems((pre) => (pre = [...items, ...temp]));
        setLoading(false);
      }, 1000);
    }
    console.log(scrollHeight, scrollTop, e.target.clientHeight);
  }
  // useEffect(()=>{
  //   window.addEventListener("scroll", infiniteScroll);
  // },[page, loading])

  console.log(page);
  console.log(items);
  //console.log(data);
  return (
    <div className="App" onScroll={infiniteScroll} style={{height: "100vh", overflow:'scroll'}}>
      {items.map((ele, idx) => (
        <div style={{ border: "1px solid black" }} key={idx + idx}>
          <h1>{idx}</h1>
          <h3>{ele.name}</h3>
          <h4>{ele.city}</h4>
          <h5>{ele.pincode}</h5>
          <h5>{ele.country}</h5>
        </div>
      ))}
      {loading && (
        <div>
          <h1>Loading...</h1>
        </div>
      )}
    </div>
  );
};

export default App;
