    },
  ],
   userInfo: {
      name: "",
      email: "",
      mobile: "",
      city: "",
    },

};


const actionReducer = (state = intialState, action)=>{
    switch(action.type){
        case "ADD_TODO": 
            return {
                ...state,
                todoList: [...state.todoList, state.userInfo],
                userInfo: {
                    name:"",
                    email:"",
                    mobile:"",
                    city: "",
                }
            }
        default :
            return 
    }
}

export default actionReducer;
