import React, {useState} from 'react'
import {ADD_TODO} from "../redux/actionType.js";
import { useDispatch} from "react-redux";
import { useNavigate } from 'react-router-dom';




export default function AddTodo() {
  const dispatch = useDispatch();
  const [userInfo, setUserInfo] = useState(
    {
      name: "",
      email: "",
      mobile: "",
      city: "",
    }
  )
  const updateInfo = (e)=>{
    setUserInfo({...userInfo, [e.target.name]: e.target.value});
  }
  const navigate = useNavigate();
  console.log(userInfo);
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <input type="text" name ="name" onChange={updateInfo} placeholder="Name..." />
      {/* <input type="text" name ="email" onChange={updateInfo} placeholder="Email..." />
      <input type="text" name ="mobile" onChange={updateInfo} placeholder="PhoneNo..." />
      <input type="text" name ="city" onChange={updateInfo} placeholder="City..." /> */}
      <button onClick={()=>{dispatch(ADD_TODO(userInfo)); navigate("/")}}>Add</button>
    </div>
  );
}
