import React, {useState} from 'react'
import { useDispatch } from 'react-redux';
import {ADD_TODOfunc} from "../redux/actionType";
import { useNavigate } from 'react-router-dom';

export default function AddTodo() {
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    city: "",
  })
  const navigate = useNavigate();
  const dispatch = useDispatch();


  const handleSubmit = (e)=>{
    setUserInfo({...userInfo, [e.target.name]: e.target.value});
  }

  const addTodo = ()=>{
    dispatch(ADD_TODOfunc(userInfo));
    navigate("/")
  }

  return (
    <div style={{display: "flex", flexDirection:"column"}}>
      <input type="text" name ="name" placeonChange={handleSubmit}/>
      <input type="text" name ="email" onChange={handleSubmit}/>
      <input type="text" name ="city" onChange={handleSubmit}/>
      <button onClick={addTodo}>ADD TODO</button>
    </div>
  );
}
