import React from 'react'

const FillTheBlank = ({ questions, setQuestions, setPage, page }) => {

  function handleAnswer(e) {
    let temp = [...questions];
    temp[2].answer = e.target.value;
    setQuestions(temp);
  }  

  return (
    <div>
      <h1>{questions[2].question}</h1>
      <input onChange={handleAnswer} placeholder="answer..." defaultValue={questions[2]}/>
      <button onClick={()=>setPage(page-1)}>Prev</button>
    </div>
  );
};

export default FillTheBlank
