const intialstate = {
    todoList: [],
}
export default function todoListReducer(state=intialstate, action){
    switch(action.type){
        
        case "ADD_TODO":
            return {
                ...state,
                todoList : [...state.todoList, action.value],
            }
        case "DELETE_TODO": 
            return {
                ...state,
                todoList : state.todoList.filter((itm, ind) => ind!==action.value),
            }
        default :
            return state

    }
}
