  const handleTimer = () => {
    setArr([...arr, 0]);
  };

  console.log(array)
  return (
    <div className='App'>
      <button onClick={handleTimer}>Add Timer</button>
      {
        arr.map((ele, idx)=>{
          return (


              !array.includes(idx) && <div>
              <Timer/>
              <button onClick={()=>{setArray([...array, idx])}}>Del</button>
            </div>
        )

        })
      }

    </div>
  )

}


export default App;
