import React, {useEffect, useState} from 'react'

export default function App() {
  const [count, setCount] = useState(0);
  let k;
  const handleClick = ()=>{
    useEffect(()=>{
      k = setInterval(()=>{
        setCount(count+1);
      }, 1000);
    })
  }
  const stopClick = ()=>{
    clearInterval(k);
    setCount(0);
  }
  return (
    <div>
      <h1>{count}</h1>
      <button onClick={handleClick}>start</button>
      <button onClick={stopClick}>stop</button>
    </div>
  )
}
