import React, {useReducer} from 'react';
import './App.css';

const intialState = 0;
const reducer = (state, action)=>{
  switch(action) {
    case 'increment' :
      if(type)
      return state + 1;
    case 'decrement' :
      if(state <1 || typeof state !=='number'){
        return "cant reduce further";
      }else{
        return state-1;
      }
    case 'reset':
      return intialState;
    default :
      return state;

  }

}

function App() {
  const [count, dispatch] = useReducer(reducer, intialState);
  return (
    <div className="App">
      <div>{count}</div>
        <button onClick={()=>dispatch('increment')}>Increment</button>
        <button onClick={()=>dispatch('decrement')}>Decrement</button>
        <button onClick={()=>dispatch('reset')}>Reset</button>
    </div>
  );
}

export default App;
