import React, { useEffect, useState } from 'react'

export default function Timer() {
    const [count, setCount] = useState(0);
    useEffect(()=>{
        const k = setInterval(()=>setCount((prev)=>prev+1), 1000);
        return ()=>clearInterval(k)
    },[count])
  return (
    <div>
        <h1>{count}</h1>
        <button>Del</button>
    </div>
  )
}
